                    onClick={handleSubmit} >
                    Log in
                </button>
            </form>
            <div className="flex flex-grow lg:flex-grow-0 px-6 items-center md:w-1/2 h-max  w-full md:min-w-[500px] max-w-[500px] mt-20">
